insert  into `app_quotes`(`ID`,`BODY`) values (53,'Великий человек идет впереди своего времени, умный идет рядом с ним, хитрый старается использовать его, глупый становится ему поперек дороги. Э. Бауэрнфельд\r');
insert  into `app_quotes`(`ID`,`BODY`) values (54,'Великодушного человека отличает то, что он не ищет выгоды для себя, но с готовностью делает добро другим. Аристотель\r');
