insert  into `app_quotes`(`ID`,`BODY`) values (63,'Видеть несправедливость и молчать - это значит, самому участвовать в ней. Ж. Ж. Руссо\r');
insert  into `app_quotes`(`ID`,`BODY`) values (64,'Вместо того, чтобы сетовать, что роза имеет шипы, я радуюсь тому, что среди шипов растет роза. Ж. Жубер\r');
insert  into `app_quotes`(`ID`,`BODY`) values (65,'Во всем, как хотите, чтобы с вами поступали люди, так поступайте и вы с ними, ибо в этом закон. Библия, Левит\r');
insert  into `app_quotes`(`ID`,`BODY`) values (66,'Во всяком творении гения мы узнаем собственные отвергнутые мысли. Ральф Уолдо Эмерсон\r');
